import React, { useState, useEffect } from 'react';
import axios from 'axios';
import "../styles/BookJourney.css"
import { act } from 'react-dom/test-utils';


const BookJourney = ({ bookId }) => {
  const [book, setBook] = useState({});
  const [journey, setJourney] = useState({});

  useEffect(() => {
    axios.get(`/api/books/${bookId}`).then((response) => {
      setBook(response.data);
    });
    axios.get(`/api/books/${bookId}/journey`).then((response) => {
      setJourney(response.data);
    });
  }, [bookId]);

  const handleSave = () => {
    axios.put(`/api/books/${bookId}/journey`, journey).then(() => {
      // Success message or redirection
    });
  };

  const handleAddActor =(actor)=>{
    setJourney({...journey, favoriteActors: [...journey.favoriteActors, actor]})
  }

  const handleAddDialogue = (dialogue) => {
    setJourney({ ...journey, favoriteDialogues: [...journey.favoriteDialogues, dialogue] });
  };

  const handleAddQuote = (quote) => {
    setJourney({ ...journey, favoriteQuotes: [...journey.favoriteQuotes, quote] });
  };

  return (
    <div className="book-journey">
      <h1>{book.title}</h1>
      <img src={book.coverImage} alt={book.title} />
      <div className="journey-form">
        <h2>My Journey</h2>
        <label htmlFor="thoughts">Thoughts:</label>
        <textarea
          id="thoughts"
          value={journey.thoughts}
          onChange={(e) => setJourney({ ...journey, thoughts: e.target.value })}
        />
        <label htmlFor="summary">Summary:</label>
        <textarea
          id="summary"
          value={journey.summary}
          onChange={(e) => setJourney({ ...journey, summary: e.target.value })}
        />
        <label htmlFor="previews">Previews:</label>
        <textarea
          id="previews"
          value={journey.previews}
          onChange={(e) => setJourney({ ...journey, previews: e.target.value })}
        />
        <label htmlFor="reviews">Reviews:</label>
        <textarea
          id="reviews"
          value={journey.reviews}
          onChange={(e) => setJourney({ ...journey, reviews: e.target.value })}
        />
        <button onClick={handleSave}>Save</button>

        <div className="favorite-actors">
          <h3>Favorite Actors</h3>
          <ul>
            {journey.favoriteActors && journey.favoriteActors.map(actor => <li key={actor}>{actor}</li>)}
          </ul>
          <button onClick={handleAddActor}>Add Actor</button>
        </div>

        <div className="favorite-dialogues">
          <h3>Favorite Dialogues</h3>
          <ul>
            {journey.favoriteDialogues && journey.favoriteDialogues.map(dialogue => <li key={dialogue}>{dialogue}</li>)}
          </ul>
          <button onClick={handleAddDialogue}>Add Dialogue</button>
        </div>

        <div className="favorite-quotes">
          <h3>Favorite Quotes</h3>
        </div>
      </div>
    </div>
  );
};

export default BookJourney;
